export default class{constructor(t=100,i=!0){this.duration=1e3*t,this.oldTime=0,this.paused=i,this.startTime=0}runningTime(){if(!1===this.paused){const t=Date.now()-this.startTime;return Number(t)}return Number(this.oldTime)}play(){!0===this.paused&&(this.startTime=Date.now()-this.oldTime,this.oldTime=0,this.paused=!1)}pause(){!1===this.paused&&(this.oldTime=Date.now()-this.startTime,this.startTime=0,this.paused=!0)}stop(){this.oldTime=0,this.startTime=0,this.paused=!0}resume(){this.play()}forward(t=5e3){let i=!1;!0===this.paused&&(i=!0),this.pause(),this.oldTime+t<this.duration&&(this.oldTime=this.oldTime+t),0==i&&this.play()}rewind(t=5e3){let i=!1;!0===this.paused&&(i=!0),this.pause(),this.oldTime-t>0&&(this.oldTime=this.oldTime-t),0==i&&this.play()}}
